import groovy.json.JsonSlurper
import com.groovy.*
import java.lang.* 
import jxl.*
import eviware.soapui.model.*


def get_product_res = context.testCase.getTestStepByName("GetProduct").getPropertyValue("response")

def holder = new JsonSlurper().parseText(get_product_res)

for (def member : holder.data){
	if(member.quantity<10)
	{ 
	def id = member.id.toString()
     def quantity = (member.quantity+1000).toString()
     log.info id
     log.info quantity
	context.testCase.setPropertyValue("id",id)
	context.testCase.setPropertyValue("quantity",quantity)

	testRunner.runTestStepByName("Update Quantity")
	log.info "Update Successfully"

	testRunner.runTestStepByName("GetProduct")

     def post_update_res = context.testCase.getTestStepByName("GetProduct").getPropertyValue("response")
     def holder1 = new JsonSlurper().parseText(post_update_res)
     for (def member1 : holder1.data){
	     if(member1.id==member.id){ 
		def quantity_new=member1.quantity.toString()
		log.info "quantity_new  " +quantity_new
		context.testCase.setPropertyValue("updated_quantity",quantity_new)
		break
	     }
          }
     def up_qty =context.testCase.getPropertyValue("updated_quantity")
	assert quantity == up_qty
	 log.info "updated value is validated with response"
	}

else 
 {
		log.info "Quantity update is not performed"
	}
}

